import React, { useState, useEffect } from 'react';
import { postAPI, authAPI } from '../../utils/api';
import CreatePostButton from '../SmallerComponents/CreatePostButton';
import { FaEllipsisV } from 'react-icons/fa';
import { Link } from 'react-router-dom';
import './Profile.css';

function ProfilePage() {
  const [user, setUser] = useState(null);
  const [posts, setPosts] = useState([]);
  const [activeTab, setActiveTab] = useState('named');
  const [loading, setLoading] = useState(true);

  // menu and editing state
  const [menuOpen, setMenuOpen] = useState(null);
  const [editingPost, setEditingPost] = useState(null);
  const [editContent, setEditContent] = useState('');

  useEffect(() => {
    // Check if user is logged in
    const token = localStorage.getItem('token');
    const userData = localStorage.getItem('user');

    if (token && userData) {
      const parsedUser = JSON.parse(userData);
      setUser(parsedUser);
      fetchUserPosts(parsedUser._id);
    } else {
      setLoading(false);
    }
  }, []);

  const fetchUserPosts = async (userId) => {
    try {
      // Get all posts and filter by current user
      const response = await postAPI.getAllPosts();
      const userPosts = response.data.filter(post => post.author._id === userId);
      setPosts(userPosts);
    } catch (error) {
      console.error('Failed to fetch posts:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleNewPost = () => {
    // Refresh posts after creating new one
    if (user) {
      fetchUserPosts(user._id);
    }
  };

  const toggleMenu = (postId) => {
    setMenuOpen(menuOpen === postId ? null : postId);
  };

  const handleDelete = async (postId) => {
    try {
      await postAPI.deletePost(postId);
      setPosts(posts.filter((p) => p._id !== postId));
      setMenuOpen(null);
    } catch (error) {
      console.error('Failed to delete post:', error);
      alert('Failed to delete post');
    }
  };

  const startEditing = (post) => {
    setEditingPost(post);
    setEditContent(post.content);
    setMenuOpen(null);
  };

  const saveEdit = async (postId) => {
    try {
      await postAPI.updatePost(postId, { content: editContent });
      setPosts(
        posts.map((p) =>
          p._id === postId ? { ...p, content: editContent } : p
        )
      );
      setEditingPost(null);
    } catch (error) {
      console.error('Failed to update post:', error);
      alert('Failed to update post');
    }
  };

  if (loading) {
    return (
      <div className="profile-page">
        <div className="loading" style={{ textAlign: 'center', padding: '2rem' }}>
          Loading profile...
        </div>
      </div>
    );
  }

  if (!user) {
    return (
      <div className="profile-page">
        <div className="login-prompt-card">
          <h2>✨ Join the Scriblyn Community</h2>
          <p>Sign up or log in to view your profile, post history, and connect with others.</p>
          <Link to="/login">
            <button className="primary-btn">Login / Signup</button>
          </Link>
        </div>
      </div>
    );
  }

  const namedPosts = posts.filter((post) => !post.anonymous);
  const anonymousPosts = posts.filter((post) => post.anonymous);
  const postsToShow = activeTab === 'named' ? namedPosts : anonymousPosts;

  return (
    <div className="profile-page">
      <div className="profile-header">
        <img src={userData.profilePic} alt="profile" />
        <h2>{userData.name}</h2>
        <p>{userData.bio}</p>
        <div className="profile-stats">
          <span><strong>{posts.length}</strong> posts</span>
          <span><strong>{userData.followersCount}</strong> followers</span>
          <span><strong>{userData.followingCount}</strong> following</span>
        </div>
      </div>

      {/* Create Post Button */}
      <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '1rem' }}>
        <CreatePostButton onPost={handleNewPost} />
      </div>

      {/* Tabs */}
      <div className="profile-tabs">
        <button
          className={activeTab === 'named' ? 'active' : ''}
          onClick={() => setActiveTab('named')}
        >
          Named Posts
        </button>
        <button
          className={activeTab === 'anonymous' ? 'active' : ''}
          onClick={() => setActiveTab('anonymous')}
        >
          Anonymous Posts
        </button>
      </div>

      {/* Posts */}
      <div className="profile-posts">
        {postsToShow.map((post) => (
          <div key={post.id} className="profile-post-card">
            <div className="post-header">
              <h4>{post.title}</h4>
              <div className="post-options">
                <FaEllipsisV onClick={() => toggleMenu(post.id)} />
                {menuOpen === post.id && (
                  <div className="post-dropdown">
                    <button onClick={() => startEditing(post)}>Edit</button>
                    <button onClick={() => handleDelete(post.id)}>Delete</button>
                  </div>
                )}
              </div>
            </div>

            {editingPost?.id === post.id ? (
              <>
                <textarea
                  value={editContent}
                  onChange={(e) => setEditContent(e.target.value)}
                  rows="4"
                  style={{ width: '100%', marginTop: '0.5rem' }}
                />
                <button
                  className="primary-btn"
                  style={{ marginTop: '0.5rem' }}
                  onClick={() => saveEdit(post.id)}
                >
                  Save
                </button>
              </>
            ) : (
              <p>{post.content}</p>
            )}

            <div className="post-meta">
              <span>{new Date(post.createdAt).toLocaleDateString()}</span>
              <span>❤️ {post.likes}</span>
            </div>
            <div className="post-tags">
              {post.tags.map((tag, i) => (
                <span key={i} className="tag">#{tag}</span>
              ))}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default ProfilePage;
